import { useState } from "react";
import { useNavigate } from "react-router";
import Box from '@mui/material/Box';
import {Typography } from '@mui/material';
import { Button } from '@mui/material';
import Modal from '@mui/material/Modal';
import Stack from '@mui/material/Stack';
import TextField from '@mui/material/TextField';
import Container from '@mui/material/Container';

import background from './assets/Jemeelah2-1.svg';

export const CreateAccount = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [address, setAddress] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [createPassword, setCreatePassword] = useState('');
    const [account, setAccount] = useState('');
    const [pin, setPin] = useState('');
    const navigate = useNavigate();
    // MODAL METHODS
    const [open, setOpen] = useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    const style = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: 400,
        bgcolor: 'white',
        border: '2px solid #fff',
        boxShadow: 24,
        p: 4,
      };
 return (<div
    style = {{backgroundImage:`url(${background})`,
    height:'100vh',
    width:'100%',
    overflowX:'hidden',
    backgroundPosition:'center',
    backgroundSize:'cover',
        }}
        >
     <Container style ={{marginTop:10}}>
                <Typography variant="body1" color ='primary' style =  {{ fontSize:30}}>Create Account</Typography>
    </Container>
    <Container style ={{marginTop:30, marginBottom:20}}>
        <form>
            <Stack spacing ={2} style ={{width:'40%'}} >
                <TextField  variant= 'standard' label = 'First Name' value = {firstName}  />
                <TextField  variant= 'standard' label = 'Last Name' value = {lastName} />
                <TextField  variant= 'standard' label = 'Address' value = {address} />
                <TextField  variant= 'standard' label = 'Phone Number' type = 'number' value = {phoneNumber} />
                <TextField  variant= 'standard' label = 'Email' type = 'email' value = {email} />
                <TextField  variant= 'standard' label = 'Password' id="filled-password-input" type="password"  value = {password} />
                <TextField  variant= 'standard' label = 'Create Password' id="filled-password-input" type="password" value = {createPassword}  />
                <TextField  variant= 'standard' label = 'Account Type' type = 'text' value = {account} />
                <TextField  variant= 'standard' label = 'Pin' id="filled-password-input" type="password" value ={pin}  />
                <Button variant='contained' disableRipple color = 'primary' onClick={handleOpen}>Create Account</Button>
                <Modal
                                open={open}
                                onClose={handleClose}
                                aria-labelledby="modal-modal-title"
                                aria-describedby="modal-modal-description"
                            >
                                <Box sx={style}>
                                    <Stack>
                                    <Typography variant="body1" color ='primary' style =  {{ fontSize:30}}>Account Created successfully</Typography>
                                    </Stack>
                                    <Stack>
                                        <Typography>Click OK to navigate to login page</Typography>
                                    </Stack>
                                    
                                </Box>
                            </Modal>
            </Stack>
        </form>
    </Container>
 </div>);
}
