import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { useState ,useEffect} from "react";
import { Typography } from '@mui/material';
import Grid from '@mui/material/Grid';
import  { makeStyles } from '@mui/styles';
import TextField from '@mui/material/TextField';
import Container from '@mui/material/Container';
import Error from "../ErrorPage";

const BankForm = () => {
    const {placeholder} = useParams();
    const [group,setGroup] = useState();
    
    const groupDetails = useSelector(state => state.transfer);
    console.log(groupDetails);
    useEffect(() => {
        function destGroup () {
            if(!placeholder || !groupDetails) {
                return <Error/>
            }else{
                 setGroup(groupDetails.filter(val => val.group === placeholder));
            }
        }
        destGroup();
        console.log(group);

    }, []);
    console.log(group[0].receiver);

    const multipleForms = () => {        
        for(let i = 0; i < group[0].receiver; i++) {
            return (<>
                        <Grid item md={6} xs = {12} justifyContent= 'center' key = {i}>
                            <TextField id="outlined-basic" label="Account Number" variant="outlined" style = {{marginLeft: 20, width:300,marginBottom:10}} />
                            <TextField id="outlined-basic" label="Bank Name" variant="outlined" style = {{marginLeft: 20, width:300,marginBottom:10}} />
                            <TextField id="outlined-basic" label="Amount" variant="outlined" style = {{marginLeft: 20, width:300,marginBottom:10}} />
                        </Grid>
                    </>);
        }
    }

    return (<div>
                <Container>
                    <Typography variant="body1" color ='primary' style =  {{marginLeft: 20, marginTop:50, fontSize:20}}>Multiple Transfer</Typography>
                </Container>
                <Container>
                    <Grid container spacing = {3} direction="row" style ={{marginTop:50, fontSize:20}} alignItems = 'center'>
                        {/* {multipleForms()} */}
                    </Grid>
                </Container>
    </div>);
}
export default BankForm;
