import { Typography } from "@mui/material";
import { useEffect,useState } from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";


import mail from '../../assets/XMLID_1_.svg';
import tangle from '../../assets/Vector-1.svg';
import trust from '../../assets/Group-3.svg'
import transfer from '../../assets/Group-4.svg'

import card from '../../assets/Group-5.svg'

const Dashboard = () => {
    const [time,setTime] = useState('');
    const navigate = useNavigate();
    const user = useSelector(state => state.reducer);

    useEffect(() => {
        const greeting = () => {
            console.log(user);
            if( window.location.pathname ==='/welcome' && user.length < 1 )    {
                return navigate('/');
            }
            // get time
            let time = new Date().getHours();
            // if t < 12AM (morning)
            if(time < 12)   {
                setTime('morning');
            }else if(time >= 12 && time < 18) {
            setTime('afternoon');
            }else if (time >=18 && time <= 23) {
             setTime('evening');
            }else {
                setTime('');
            }
    
        }
        greeting();
    }, []);
    return (<>
        {user.length && <Typography variant="body1" color ='primary' style =  {{marginLeft: 200, marginTop:50, fontSize:20}}>Good {time}, { user[0].firstName}</Typography>}
        <Typography variant="h6" color ='primary' style =  {{marginLeft: 200, marginTop:50, fontSize:20}}>Talk to us</Typography>
        <Typography variant="h6"  style =  {{marginLeft: 200, fontSize:20}}>We are happy to assist</Typography>
        
        <div style={{display:'flex',marginLeft: 200, marginTop: 10}}>
            <div>
            <img src={mail} alt="mail" style={{width:30,height:30, verticalAlign:'bottom'}} />
            <span style = {{marginLeft:5}}>Help @xmt.ng</span>
            </div>
            <div style={{marginLeft:30}}>
            <img src={tangle} alt="mail" style={{width:30,height:30, verticalAlign:'bottom'}} />
            <span style = {{marginLeft:5}}>0700000001</span>
            </div>
        </div>
        </>);
}
export default Dashboard;
