import { useEffect } from "react";

const Dashboard = () => {
    const [time,setTime] = useS
    useEffect(() => {
        const greeting = () => {
            // get time
            let time = new Date().getHours();
            // if t < 12AM (morning)
            if(time < 12)   {
                setTime('morning');
            }else if(time >= 12 && time < 18) {
            setTime('afternoon');
            }else if (time >=18 && time <= 23) {
             setTime('evening');
            }else {
                setTime('');
            }
    
        }
        greeting();
    });
    return (
        <div>Dashboard works!</div>
    );
}
export default Dashboard;
