import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { useState ,useEffect} from "react";
import { Typography } from '@mui/material';
import Grid from '@mui/material/Grid';
import  { makeStyles } from '@mui/styles';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import axios from "axios";
import Select from '@mui/material/Select';
import TextField from '@mui/material/TextField';
import Container from '@mui/material/Container';

import Error from "../ErrorPage";

const styles = makeStyles(theme => (
    {
        selectRoot: {
            backgroundColor:'white',
        }
    }
));

const BankForm = () => {
    const {placeholder} = useParams();
    const [groups,setGroup] = useState([]);
    const [options,setOptions] = useState([]);
    const [formNo,setFormNo] = useState(0);
    const groupDetails = useSelector(state => state.transfer);
    const [selectedBank,setSelectedBank] = useState('')

    console.log(groupDetails);
    useEffect(() => {
        function destGroup () {
            if(!placeholder || !groupDetails) {
                return <Error/>
            }else{
                 setGroup(groupDetails.filter(val => val.group === placeholder));
            }
        }
        destGroup();
        // bankName();
    }, []);


    const classes = styles();

    return (<div>
                <Container>
                    <Typography variant="body1" color ='primary' style =  {{marginLeft: 20, marginTop:50, fontSize:20}}>Multiple Transfer</Typography>
                </Container>
                <Container>
                    <Typography variant="body1" style =  {{marginLeft: 20,fontSize:20, marginTop:30}}>{placeholder}  ({(groupDetails.filter(val => val.group === placeholder))[0].receiver}/10)</Typography>
                </Container>
                <Container>
                    <Grid container spacing = {3} direction="row" style ={{marginTop:50, fontSize:20}} alignItems = 'center'>
                        <Grid item md={6} xs = {12} justifyContent= 'center'>
                            <TextField id="outlined-basic" label="Account Number" variant="outlined" style = {{marginLeft: 20, width:300,marginBottom:10}} />
                            <FormControl style =  {{marginLeft: 20, width:300}}>
                                <InputLabel id="demo-simple-select-label" style = {{fontSize:15}}>Bank Name</InputLabel>
                                    <Select
                                    classes ={{ select: classes.selectRoot }}
                                    labelId="demo-simple-select-label"
                                    id="demo-simple-select"
                                    value={selectedBank}
                                    label="Recipient No"
                                    onChange = {e => setSelectedBank(e.target.value)}
                                    elevation = {0}
                                    >
                                    {options.map((val,key) => (
                                    <MenuItem elevation = {0} key = {key} value={val} style = {{color:'white'}}>{val}</MenuItem>
                                    ))}
                                    </Select>
                            </FormControl>
                            <TextField id="outlined-basic" label="Amount" variant="outlined" style = {{marginLeft: 20, width:300,marginBottom:10}} />
                        </Grid>
                    </Grid>
                </Container>
    </div>);
}
export default BankForm;
