                                    label="Recipient No"
                                    onChange = {e => setSelectedBank(e.target.value)}
                                    elevation = {0}
                                    >
                                    {options.map((val,key) => (
                                    <MenuItem elevation = {0} key = {key} value={val} style = {{color:'white'}}>{val}</MenuItem>
                                    ))}
                                    </Select>
                            </FormControl>
                            <TextField id="outlined-basic" label="Amount" variant="outlined" style = {{marginLeft: 20, width:300,marginBottom:10,marginTop:10}} />
                            <TextField id="outlined-basic" label="Account Name" variant="outlined" style = {{marginLeft: 20, width:300,marginBottom:10}} />
                            </div>}

                        </Grid>
                    </Grid>
                </Container>

    </div>);
}
export default BankForm;
