// import Box from '@mui/material/Box';
// import Grid from '@mui/material/Grid';
import login from '../assets/banner.svg';
import TextField from '@mui/material/TextField';
import { makeStyles } from '@mui/styles';
import { useState,useEffect } from 'react';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import menuLogo from '../assets/official-logo.png';
import instagram from '../assets/instagram.png';
import twitter from '../assets/twitter.png';
import facebook from '../assets/facebook.png'
import Typography from '@mui/material/Typography';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { loginAction } from '../redux/action/action';
import axios from "axios";
// import useVH from 'react-viewport-height';

// import OutlinedInput from '@mui/material/OutlinedInput';
// import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormControl from '@mui/material/FormControl';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
// import { padding } from '@mui/system';

  const useStyle = makeStyles(theme => ({
      textCenter: {
          textAlign: 'center',
          color:theme.palette.primary.light
      },
      loginImg:{
          maxWidth: '100%',
          width: '100%',
          maxHeight:'100vh',
          height:'100vh',
          backgroundColor:'black',
          backgroundImage: `url(${login})`,
          backgroundPosition:'center',
          backgroundAttachment: 'fixed',
          backgroundRepeat: 'no-repeat',
          backgroundSize:'cover',
          [theme.breakpoints.down('md')]: {
            display:'none',
          }
      },
      borderInput: {
          borderColor: theme.palette.primary.main
      },
      flexText: {
          display: 'flex',
          justifyContent: 'space-between'
      },
      pinkColor: {
          color: '#F62658',
          marginLeft: '200px'
      },
      inputField: {
          border: `2px solid ${theme.palette.primary.main}`,
      },
      gridBox:  {
        //   display:'flex',
        //   justifyContent: 'center',
        //   alignItems: 'center',
        height:200,
        width:'100%',
          [theme.breakpoints.down('md')]: {
              backgroundColor:`${theme.palette.primary.main}`
          }
      },
      wrapper: {
          display:'grid',
          gridTemplateColumns:`auto auto`,
          gridColumnGap:'10px'
      },
    //   loginForm:{
    //     [theme.breakpoints.down('xs')]: {
    //         width:'50%',
    //       }
    //   }
  }));
const Home = (props) => {
    const [values, setValues] = useState({
        password: '',
        showPassword: false,
      });
    const [email,setEmail] = useState('');
    const [passwordError,setPasswordError] = useState(false);
    const [emailError,setEmailError] = useState(false);
    const [data,setData] = useState([]);
    const [warning,setWarning] = use
    const navigate = useNavigate();
    const dispatch = useDispatch();
    // const vh = useVH();


    useEffect(() => {
        async function fetchApi () {
             ( await axios.get('https://localhost:44322/Customers').then
            (response => (JSON.stringify(response.data.value))).then(response => setData(response)).catch
            (error => console.log(error)));
        }
        fetchApi();

    }, [])

    
      const handleChange = (prop) => (event) => {
        setValues({ ...values, [prop]: event.target.value });
      };
      const handleClickShowPassword = () => {
          setValues({
              ...values,
              showPassword: !values.showPassword,
            });
        };
        
      const handleMouseDownPassword = (event) => {
        event.preventDefault();
      };

      const handleEmail = (e) => {
          setEmail(e.target.value);
      }
      const handleSubmit = (e) => {
          e.preventDefault();
        if(values.password === '' || email === ''){
            if (values.password === '' ) {
             return    setPasswordError(!passwordError);
            }
            if (email === '' ) {
             return setEmailError(!emailError);
            }

        }
        if(data){ 
            let user = JSON.parse(data);
            let validData = user.filter(val => val.customerEmail === email);
            console.log(validData);
            if(validData.length > 0){
                dispatch(loginAction(...validData));
                setWarning(false);
                navigate('/welcome/Dashboard');
            }else{
                setWarning(true);
            }
        }
      }

