import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import login from '../assets/login-banner-2.jpg';
import TextField from '@mui/material/TextField';
import { makeStyles } from '@mui/styles';
import { useState } from 'react';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import menuLogo from '../assets/official-logo.png';
import instagram from '../assets/instagram.png';
import twitter from '../assets/twitter.png';
import facebook from '../assets/facebook.png'
import Typography from '@mui/material/Typography';
// import OutlinedInput from '@mui/material/OutlinedInput';
// import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormControl from '@mui/material/FormControl';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';

  const useStyle = makeStyles(theme => ({
      textCenter: {
          textAlign: 'center',
          color:theme.palette.primary.light
      },
      loginImg:{
          maxWidth: '100%',
          width: '700px',
          height:'650px',
          backgroundImage: `url(${login})`,
      },
      borderInput: {
          borderColor: theme.palette.primary.main
      },
      flexText: {
          display: 'flex',
          justifyContent: 'space-between'
      },
      pinkColor: {
          color: '#F62658',
          marginLeft: '200px'
      },
      inputField: {
          border: `2px solid ${theme.palette.primary.main}`,
      }
  }));
const Home = (props) => {
    const [values, setValues] = useState({
        password: '',
        showPassword: false,
      });
    
      const handleChange = (prop) => (event) => {
        setValues({ ...values, [prop]: event.target.value });
      };
    
      const handleClickShowPassword = () => {
        setValues({
          ...values,
          showPassword: !values.showPassword,
        });
      };
    
      const handleMouseDownPassword = (event) => {
        event.preventDefault();
      };
    const classCust = useStyle();
    return (<div>
        <Box sx={{ flexGrow: 1 }}>
            <Grid container rowSpacing={3}>
                <Grid item xs = {12} md = {6} className={classCust.loginImg} >
                    <div style = {{display:'flex', justifyContent: 'center', width:'100%', height:30, marginBottom:0}}>
                        <img src={menuLogo} alt="xmt logo" style = {{width: 100, height: 100}} />
                    </div>
                    <article style = {{marginTop:'150px'}}>
                        <div style = {{marginTop:30}}>
                            <Typography variant='h4' color = 'secondary' style = {{fonstSize}}>Sign up seamlessly</Typography>
                        </div><br />
                        <div>
                            <Typography variant='h6' color = 'secondary'>Open an ALAT account from your phone with your BVN and phone 
                            number, no paperwork required.
                            </Typography>
                        </div>
                        <div style = {{display:'flex', justifyContent: 'center'}}>
                            <Button variant="contained" color = 'secondary' background = 'primary' disableRipple>Get Started</Button>
                        </div>
                        <div style = {{display: 'flex', justifyContent: 'space-evenly', marginTop:'30px'}}>
                            <img src={instagram} alt="" />
                            <img src={facebook} alt="" />
                            <img src={twitter} alt="" />
                        </div>
                    </article>
                    {/* <div style = {{display:'flex', justifyContent: 'center', width:'100%', marginTop: 0,border: '1px solid red', height: '20px'}}>
                        <Typography variant='span' color = 'secondary' style = {{padding:2, marginLeft:10, border: '1px solid green'}}>
                            Open an ALAT account from your phone with your BVN and phone 
                        number, no paperwork required.
                        </Typography>
                    </div> */}
                </Grid>
                <Grid container item xs = {12} md = {6} style = {{marginTop:'50px'}}>
                        <div style = {{marginLeft:'20px'}}>
                        <h3 className={classCust.textCenter} >Go to XMT Website</h3>
                        <p style = {{marginLeft:'20px'}}>Welcome Back!</p>
                        <div>
                            <form >
                                <div>
                                    <p>
                                        Email Address/Username
                                    </p>
                                    <TextField id="outlined-basic" 
                                    // label="Outlined" 
                                    // variant="outlined" 
                                    sx = {{width: 400,}} focused />
                                </div>
                                <div>
                                    <div style = {{marginTop:'20px'}} >
                                        <span>Password</span>
                                        <span className={classCust.pinkColor}>Forgot Password?</span>
                                    </div>
                                    <FormControl sx={{ m: 1, width: '25ch' }} variant="outlined"/>
                                    <div></div>
                                    <TextField
                                        id="outlined-adornment-password"
                                        type={values.showPassword ? 'text' : 'password'}
                                        value={values.password}
                                        className = {classCust.inputField}                                   
                                        onChange={handleChange('password')}
                                        endAdornment={
                                        <InputAdornment position="end">
                                            <IconButton
                                            aria-label="toggle password visibility"
                                            onClick={handleClickShowPassword}
                                            onMouseDown={handleMouseDownPassword}
                                            edge="end"
                                            >
                                            {values.showPassword ? <VisibilityOff /> : <Visibility />}
                                            </IconButton>
                                        </InputAdornment>
                                        }
                                        sx = {{width: 400}}
                                        focused
                                    />
                                </div>
                                <Button variant="contained" sx = {{width:'100%', marginTop:'50px'}}>Login</Button>
                            </form>
                        </div>
                    </div>
                </Grid>
            </Grid>
        </Box>
    </div>);
}
export default Home;
