import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { useState ,useEffect} from "react";
import { Typography } from '@mui/material';
import Grid from '@mui/material/Grid';
import  { makeStyles } from '@mui/styles';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import { Button } from '@mui/material';
import { useRef } from "react";
import axios from "axios";
import Select from '@mui/material/Select';
import TextField from '@mui/material/TextField';
import Container from '@mui/material/Container';

import Error from "../ErrorPage";

const styles = makeStyles(theme => (
    {
        selectRoot: {
            backgroundColor:'white',
        }
    }
));

const BankForm = () => {
    const {placeholder} = useParams();
    const [groups,setGroup] = useState([]);
    const [options,setOptions] = useState([]);
    const [formNo,setFormNo] = useState(0);
    const groupDetails = useSelector(state => state.transfer);
    const [selectedBank,setSelectedBank] = useState('');
    const colRef = useRef(null);


    console.log(groupDetails);
    useEffect(() => {
        function destGroup () {
            if(!placeholder || !groupDetails) {
                return <Error/>
            }else{
                 setGroup(groupDetails.filter(val => val.group === placeholder));
            }
        }
        async function bankName () {
            return await axios.get('https://localhost:44322/Bank/get_banks').then(res =>setOptions(res.data)).catch(err => console.error(err));
        }
        destGroup();
        bankName();
        console.log(colRef,'first');
    }, []);


    const classes = styles();

    return (<div>
                <Container>
                    <Typography variant="body1" color ='primary' style =  {{marginLeft: 20, marginTop:50, fontSize:20}}>Multiple Transfer</Typography>
                </Container>
                <Container>
                    <Typography variant="body1" style =  {{marginLeft: 20,fontSize:20, marginTop:30}}>{placeholder}  ({(groupDetails.filter(val => val.group === placeholder))[0].receiver}/10)</Typography>
                </Container>
                <Container>
                    <Grid container spacing = {3} direction="row" style ={{marginTop:50, fontSize:20}}>
                         <Grid item md={6} xs = {12} id = {1} ref = {colRef}>
                            {(colRef.id && Number(colRef.id) > 0) && <div id = {2} ref = {colRef}>
                                <TextField id="outlined-basic" label="Account Number" variant="outlined" style = {{marginLeft: 20, width:300,marginBottom:10}}/>
                            <FormControl style =  {{marginLeft: 20, width:300}}>
                                <InputLabel id="demo-simple-select-label" style = {{fontSize:15}}>Bank Name</InputLabel>
                                    <Select
                                    classes ={{ select: classes.selectRoot }}
                                    labelId="demo-simple-select-label"
                                    id="demo-simple-select"
                                    value={selectedBank}
                                    label="Recipient No"
                                    onChange = {e => setSelectedBank(e.target.value)}
                                    elevation = {0}
                                    >
                                    {options.map((val,key) => (
                                    <MenuItem elevation = {0} key = {key} value={val} style = {{color:'white'}}>{val}</MenuItem>
                                    ))}
                                    </Select>
                            </FormControl>
                            <TextField id="outlined-basic" label="Amount" variant="outlined" style = {{marginLeft: 20, width:300,marginBottom:10,marginTop:10}} />
                            <TextField id="outlined-basic" label="Account Name" variant="outlined" style = {{marginLeft: 20, width:300,marginBottom:10}} />
                            </div>}
                                {/* <Grid item>
                                    <Container>
                                        <Button variant="contained" color="primary" disableRipple>Submit</Button>
                                    </Container>
                                </Grid> */}
                        </Grid>
                    </Grid>
                </Container>

    </div>);
}
export default BankForm;
