import { NoEncryption } from '@mui/icons-material';
import { createTheme } from '@mui/material/styles';
const wine = '#AB051D'
const theme = createTheme({
    palette: {
      primary: {
        main: wine,
      },
      secondary: {
          main : '#fff'
      }
    },
    
     MuiInput:  {
        root:{
             border:'none',
             "&:hover:not($disabled):not($focused):not($error):before":{
                 border:'none'
             }
        }
    }
  });
export default theme;
